import React, { useState } from "react";

export default function MyPage() {
  const [profileImage, setProfileImage] = useState("/default_profile.jpg");
  const [bio, setBio] = useState("안녕하세요! SLAM 멤버입니다.");
  const [posts, setPosts] = useState([
    { id: 1, title: "첫 번째 게시글", date: "2025-05-01" },
    { id: 2, title: "언어교환 팁 공유", date: "2025-05-12" },
  ]);
  const [comments, setComments] = useState([
    { id: 1, content: "좋은 정보 감사합니다!", postTitle: "첫 번째 게시글" },
    { id: 2, content: "정말 유용했어요!", postTitle: "언어교환 팁 공유" },
  ]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const previewURL = URL.createObjectURL(file);
      setProfileImage(previewURL);
      // 백엔드 업로드 요청도 추가 가능
    }
  };

  return (
    <div className="max-w-4xl mx-auto py-10 px-4">
      <h1 className="text-3xl font-bold mb-6">마이페이지</h1>

      {/* 프로필 사진 및 자기소개 */}
      <div className="flex gap-6 items-center mb-8">
        <div className="relative w-28 h-28">
          <img
            src={profileImage}
            alt="프로필"
            className="w-full h-full rounded-full object-cover border"
          />
          <label className="absolute bottom-0 right-0 bg-white p-1 rounded-full shadow cursor-pointer">
            📷
            <input type="file" className="hidden" onChange={handleImageChange} />
          </label>
        </div>
        <div className="flex-1">
          <textarea
            className="w-full border rounded p-2"
            rows={3}
            value={bio}
            onChange={(e) => setBio(e.target.value)}
            placeholder="자기소개를 입력하세요..."
          />
        </div>
      </div>

      {/* 작성한 게시글 */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-4">내 게시글</h2>
        <ul className="list-disc pl-5">
          {posts.map((post) => (
            <li key={post.id} className="mb-1">
              📌 {post.title} <span className="text-gray-500 text-sm">({post.date})</span>
            </li>
          ))}
        </ul>
      </div>

      {/* 작성한 댓글 */}
      <div>
        <h2 className="text-xl font-semibold mb-4">내 댓글</h2>
        <ul className="list-disc pl-5">
          {comments.map((comment) => (
            <li key={comment.id} className="mb-1">
              💬 {comment.content} <span className="text-gray-500 text-sm">(on {comment.postTitle})</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
