import React, { useState } from "react";
import MainHeader from "../components/MainHeader";

export default function MyPage() {
  const [profileImage, setProfileImage] = useState("/default_profile.jpg");
  const [bio, setBio] = useState("안녕하세요! SLAM 멤버입니다.");
  const [posts, setPosts] = useState([
    { id: 1, title: "첫 번째 게시글", date: "2025-05-01" },
    { id: 2, title: "언어교환 팁 공유", date: "2025-05-12" },
  ]);
  const [comments, setComments] = useState([
    { id: 1, content: "좋은 정보 감사합니다!", postTitle: "첫 번째 게시글" },
    { id: 2, content: "정말 유용했어요!", postTitle: "언어교환 팁 공유" },
  ]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const previewURL = URL.createObjectURL(file);
      setProfileImage(previewURL);
      // 백엔드 업로드 연동 가능
    }
  };

  return (
    <div className="font-sans text-gray-900">
      <MainHeader />

      <main className="max-w-5xl mx-auto px-6 py-12">
        <h1 className="text-3xl font-bold mb-8 text-center">마이페이지</h1>

        {/* 프로필 정보 */}
        <div className="flex flex-col sm:flex-row gap-6 items-start sm:items-center mb-10">
          <div className="relative w-32 h-32">
            <img
              src={profileImage}
              alt="프로필"
              className="w-full h-full rounded-full object-cover border"
            />
            <label className="absolute bottom-0 right-0 bg-white p-1 rounded-full shadow cursor-pointer text-xs">
              📷
              <input type="file" className="hidden" onChange={handleImageChange} />
            </label>
          </div>
          <div className="flex-1 w-full">
            <textarea
              className="w-full border rounded p-3 text-sm"
              rows={4}
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              placeholder="자기소개를 입력하세요..."
            />
          </div>
        </div>

        {/* 게시글 섹션 */}
        <section className="mb-10">
          <h2 className="text-xl font-semibold mb-4">📌 내가 작성한 게시글</h2>
          <ul className="list-disc pl-6 space-y-1 text-sm">
            {posts.map((post) => (
              <li key={post.id}>
                {post.title} <span className="text-gray-500">({post.date})</span>
              </li>
            ))}
          </ul>
        </section>

        {/* 댓글 섹션 */}
        <section>
          <h2 className="text-xl font-semibold mb-4">💬 내가 작성한 댓글</h2>
          <ul className="list-disc pl-6 space-y-1 text-sm">
            {comments.map((comment) => (
              <li key={comment.id}>
                {comment.content}{" "}
                <span className="text-gray-500">(on {comment.postTitle})</span>
              </li>
            ))}
          </ul>
        </section>
      </main>
    </div>
  );
}
