// src/api/axios.js
import axios from 'axios';

const instance = axios.create({
  baseURL: 'http://localhost:8080', // 백엔드 주소
  withCredentials: true, // JWT 쿠키 인증을 쓴다면 true, 아니라면 생략 가능
});

export default instance;
